-- =====================================================================
-- INFORMAÇÔES COMPLEMENTARES DE CARACTERÍSTICAS DE VEÍCULOS
-- =====================================================================
-- Tabelas utilizada a nível de cadastro em diversas partes.
-- =====================================================================

-- ---------------------------------------------------------------------
-- Ícones de Veículos
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.symbols (
  symbolID  serial,       -- ID do símbolo
  name      varchar(30)   -- Nome do símbolo
            NOT NULL,
  content   text          -- O conteúdo do símbolo (em SVG)
            NOT NULL,
  PRIMARY KEY (symbolID)
);


-- ---------------------------------------------------------------------
-- Tipos de Veículos
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.vehicleTypes (
  vehicleTypeID   serial,       -- ID do tipo de veículo
  name            varchar(30)   -- Nome do tipo de veículo
                  NOT NULL,
  article         char(1),      -- O artigo a ser utilizado antes do nome
  singular        varchar(30)   -- Nome do tipo de veículo no singular
                  NOT NULL,
  fipeName        varchar(30),  -- Nome do tipo de veículo no sistema FIPE
  symbol          varchar(30),  -- Nome do símbolo a ser usado no mapa
  PRIMARY KEY (vehicleTypeID)
);


ALTER SEQUENCE erp.vehicletypes_vehicletypeid_seq RESTART WITH 8;

-- ---------------------------------------------------------------------
-- Subtipos de Veículos
-- ---------------------------------------------------------------------
-- Descreve os subtipos de um determinado tipo de veículo principal. Um
-- subtipo é usado para definir melhor o ícone a ser exibido no mapa.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.vehicleSubTypes (
  vehicleSubTypeID   serial,       -- ID do subtipo de veículo
  vehicleTypeID      integer       -- O ID do tipo de veículo
                     NOT NULL,
  article            char(1),      -- O artigo a ser utilizado antes do nome
  name               varchar(30)   -- Nome do subtipo de veículo
                     NOT NULL,
  symbol             varchar(30),  -- Nome do símbolo a ser usado no mapa
  PRIMARY KEY (vehicleSubTypeID),
  UNIQUE(vehicleTypeID, name)
);


-- ---------------------------------------------------------------------
-- Tipos de combustíveis
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.fuelTypes (
  fuelType  char(1),     -- Sigla do tipo de combustível
  name      varchar(30)  -- Nome do combustível
            NOT NULL,
  PRIMARY KEY (fuelType)
);


-- ---------------------------------------------------------------------
-- Cores do veículo
-- ---------------------------------------------------------------------
-- Segundo as regras de précadastro do DENATRAN (v2.0)
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.vehicleColors (
  vehicleColorID  serial,      -- ID da cor do veículo
  name            varchar(30)  -- Nome da cor
                  NOT NULL,
  color           varchar(30), -- O nome da cor no css
  PRIMARY KEY (vehicleColorID)
);

