-- ---------------------------------------------------------------------
-- Códigos de rede móvel
-- ---------------------------------------------------------------------
-- A identificação da operadora pelo código da rede de celular (MNC)
-- obtido pelo ICCID do chip.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.mobileNetworkCodes (
  mobileNetworkCodeID   serial,   -- O ID da operadora de telefonia
  mcc                   char(3)   -- O código do país do celular (Mobile
                        NOT NULL, -- Country Code)
  mnc                   char(3)   -- O código da rede de celular (Mobile
                        NOT NULL, -- Network Code)
  mobileOperatorID      integer   -- O ID da operadora de telefonia móvel
                        NOT NULL,
  PRIMARY KEY (mobileNetworkCodeID),
  UNIQUE (mcc, mnc),
  FOREIGN KEY (mobileOperatorID)
    REFERENCES erp.mobileOperators(mobileOperatorID)
    ON DELETE RESTRICT
);

