-- ---------------------------------------------------------------------
-- Nomes dos pontos de acesso
-- ---------------------------------------------------------------------
-- O controle de APN (Access Point Name), que são as configurações que
-- permitem que o SIM Card acesse a Internet de sua operadora de celular
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS erp.accessPointNames (
  accessPointNameID     serial,      -- O ID da configuração do ponto de acesso
  mobileOperatorID      integer      -- O ID da operadora de telefonia
                        NOT NULL,    -- móvel à que pertence
  name                  varchar(30)  -- O nome da APN
                        NOT NULL,
  address               varchar(100) -- O endereço da APN
                        NOT NULL,
  username              varchar(50), -- O usuário para autenticação
  password              varchar(50), -- A senha para autenticação
  PRIMARY KEY (accessPointNameID),
  FOREIGN KEY (mobileOperatorID)
    REFERENCES erp.mobileOperators(mobileOperatorID)
    ON DELETE RESTRICT
);

