-- ---------------------------------------------------------------------
-- Os objetos geográficos
-- ---------------------------------------------------------------------
-- Os objetos geográficos para cada cliente.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS geoObjects (
  geoObjectID     serial,       -- ID do objeto geográfico
  contractorID    integer       -- ID do contratante
                  NOT NULL,
  customerID      integer       -- ID do cliente
                  NOT NULL,
  name            varchar(50)   -- O nome do objeto geográfico
                  NOT NULL,
  coordinates     GEOMETRY      -- As coordenadas do centro do objeto
                  NOT NULL,     -- geográfico
  disabled        boolean       -- Indica se o objeto geográfico está
                  NOT NULL      -- desabilitado
                  DEFAULT FALSE,
  note            text          -- Uma nota sobre o objeto geográfico
                  NOT NULL,
  createdAt       timestamp     -- A data de criação do veículo
                  NOT NULL
                  DEFAULT CURRENT_TIMESTAMP,
  createdByUserID integer       -- O ID do usuário responsável pelo
                  NOT NULL,     -- cadastro deste veículo
  updatedAt       timestamp     -- A data da última modificação
                  NOT NULL      -- deste veículo
                  DEFAULT CURRENT_TIMESTAMP,
  updatedByUserID integer       -- O ID do usuário responsável pela
                  NOT NULL,     -- última modificação deste veículo
  PRIMARY KEY (geoObjectID),
  UNIQUE (contractorID, customerID, name),
  FOREIGN KEY (contractorID)
    REFERENCES erp.entities(entityID)
    ON DELETE RESTRICT,
  FOREIGN KEY (customerID)
    REFERENCES erp.entities(entityID)
    ON DELETE RESTRICT,
  FOREIGN KEY (createdByUserID)
    REFERENCES erp.users(userID)
    ON DELETE RESTRICT,
  FOREIGN KEY (updatedByUserID)
    REFERENCES erp.users(userID)
    ON DELETE RESTRICT
);

-- ---------------------------------------------------------------------
-- Os pontos de referência
-- ---------------------------------------------------------------------
-- Os pontos de referência para cada cliente. Um ponto de referência
-- é uma coordenada geográfica que recebe um nome e um símbolo e que
-- pode ser utilizada como auxiliar na navegação. Herda de geoObjects
-- as principais propriedades.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS landmarks (
  emblemID  integer       -- ID do emblema
            NOT NULL,
  color     varchar(20)   -- A cor do emblema
            NOT NULL,
  showName  boolean       -- Indica se o nome do ponto de referência
            NOT NULL      -- deve ser exibido no mapa o tempo todo
            DEFAULT TRUE,
  FOREIGN KEY (emblemID)
    REFERENCES emblems (emblemID)
    ON DELETE RESTRICT
) INHERITS (geoObjects);

CREATE INDEX idx_landmarks_customer_valid_disabled ON public.landmarks (customerID, disabled);

