-- ---------------------------------------------------------------------
-- Formas de notificação de eventos de cerca
-- ---------------------------------------------------------------------
-- As possíveis formas de notificação de eventos de cerca geográfica:
--   NONE:    Não notificar
--   DEFAULT: Usar o comportamento padrão (apenas para veículos)
--   BOTH:    Em ambos os eventos de entrada e saída da cerca
--   ENTER:   Apenas quanto ocorrer a entrada na cerca
--   EXIT:    Apenas quanto ocorrer a saída da cerca
-- ---------------------------------------------------------------------
CREATE TYPE FenceNotification AS ENUM('NONE', 'DEFAULT', 'BOTH', 'ENTER', 'EXIT');

-- ---------------------------------------------------------------------
-- Formato geométrico de uma cerca geográfica
-- ---------------------------------------------------------------------
-- Os possíveis formatos geométricos de uma cerca geográfica:
--   CIRCLE:   Círculo
--   POLYGON:  Polígono
-- ---------------------------------------------------------------------
CREATE TYPE FenceFormat AS ENUM('CIRCLE', 'POLYGON');

-- ---------------------------------------------------------------------
-- As cercas geográficas
-- ---------------------------------------------------------------------
-- As cercas geográficas para cada cliente. Uma cerca geográfica é um
-- limite geográfico virtual em torno de uma localização física, e que
-- pode detectar quando alguém entra ou sai de um local (ou região),
-- podendo enviar alertas ou notificações em tempo real. Herda de
-- geoObjects as principais propriedades.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS geofences (
  format            FenceFormat             -- O formato geométrico da cerca
                    NOT NULL
                    DEFAULT 'POLYGON',
  radius            float                   -- O raio da cerca geográfica
                    DEFAULT NULL,           -- (apenas para CIRCLE)
  area              GEOMETRY(Polygon, 4326) -- A área da cerca geográfica
                    NOT NULL,
  notification      FenceNotification       -- A forma de notificação dos
                    NOT NULL                -- eventos de cerca
                    DEFAULT 'NONE',
  temporary         boolean                 -- Indica se a cerca geográfica
                    NOT NULL                -- é temporária
                    DEFAULT FALSE,
  showInMap         boolean                 -- Indica se a cerca geográfica
                    NOT NULL                -- deve ser exibida no mapa
                    DEFAULT TRUE,
  validForEveryone  boolean                 -- Indica se a cerca geográfica
                    NOT NULL                -- é válida para todos os
                    DEFAULT TRUE,           -- veículos do cliente
  CHECK (notification <> 'DEFAULT')
) INHERITS (geoObjects);

CREATE INDEX idx_geofences_customer_valid_disabled ON public.geofences (customerID, validForEveryone, disabled);
CREATE INDEX idx_geofences_area ON public.geofences USING GIST (area);

