-- ---------------------------------------------------------------------
-- Função para obter o ponto de referência mais próximo
-- ---------------------------------------------------------------------
-- Cria uma função que obtém o ponto de referência mais próximo de uma
-- coordenada geográfica, dentro de um raio de distância.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION getNearestLandmark(FcustomerID integer,
  Flatitude float, Flongitude float, Fradius float)
RETURNS text
AS $$
DECLARE
  nearestLandmark  RECORD;
  description  text;
BEGIN
  SELECT geoObjectID, name, public.ST_Distance(coordinates::public.geography, public.ST_SetSRID(public.ST_MakePoint(Flongitude, Flatitude), 4326)::public.geography) as distance
    INTO nearestLandmark
    FROM public.landmarks
   WHERE customerID = FcustomerID
     AND public.ST_DWithin(
           coordinates::public.geography,
           public.ST_SetSRID(
             public.ST_MakePoint(Flongitude, Flatitude),
             4326
           )::public.geography,
           Fradius
         )
     AND disabled = FALSE 
   ORDER BY distance
   LIMIT 1;

  IF NOT FOUND THEN
    -- RAISE NOTICE 'No landmark found';
    RETURN NULL;
  END IF;

  -- RAISE NOTICE 'nearestLandmark: %', nearestLandmark;

  SELECT CONCAT(
           CASE 
             WHEN nearestLandmark.distance < 20 THEN ' em '
             WHEN nearestLandmark.distance < 30 THEN ' muito próximo de '
             WHEN nearestLandmark.distance < 1000 THEN CONCAT('à ', ROUND(nearestLandmark.distance), ' metros de ')
             ELSE CONCAT('à ', ROUND(nearestLandmark.distance / 1000), ' km de ')
           END, nearestLandmark.name)
    INTO description;
  
  RETURN description;
END;
$$ LANGUAGE plpgsql;

