-- ---------------------------------------------------------------------
-- Os equipamentos que estão dentro de uma cerca geográfica
-- ---------------------------------------------------------------------
-- Armazena os equipamentos que estão dentro de uma cerca geográfica.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS vehiclesInFences (
  vehicleID   integer       -- ID do veículo
              NOT NULL,
  fenceID     integer       -- ID da cerca geográfica
              NOT NULL,
  insertedAt  timestamp     -- A data de inserção do veículo na cerca
              NOT NULL
              DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (vehicleID, fenceID),
  FOREIGN KEY (vehicleID)
    REFERENCES erp.vehicles (vehicleID)
    ON DELETE CASCADE
);

CREATE INDEX idx_vehicles_in_fences ON public.vehiclesInFences (
  vehicleID,
  fenceID
);

-- ---------------------------------------------------------------------
-- Os veículos habilitados por cerca geográfica
-- ---------------------------------------------------------------------
-- Armazena os veículos habilitados por cerca geográfica. Se não tiver
-- nenhuma entrada para um veículo, significa que a cerca geográfica irá
-- atuar em todos os veículos do cliente.
-- ---------------------------------------------------------------------
CREATE TABLE IF NOT EXISTS vehiclesEnabledByFences (
  customerID    integer           -- ID do cliente
                NOT NULL,
  vehicleID     integer           -- ID do veículo
                NOT NULL,
  fenceID       integer           -- ID da cerca geográfica
                NOT NULL,
  notification  FenceNotification -- A forma de notificação dos
                NOT NULL          -- eventos de cerca
                DEFAULT 'NONE',
  enabledAt     timestamp         -- A data de habilitação do veículo
                NOT NULL
                DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (customerID, vehicleID, fenceID),
  FOREIGN KEY (customerID)
    REFERENCES erp.entities (entityID)
    ON DELETE CASCADE,
  FOREIGN KEY (vehicleID)
    REFERENCES erp.vehicles (vehicleID)
    ON DELETE CASCADE
);

CREATE INDEX idx_vehicles_enabled_fences ON public.vehiclesEnabledByFences (
  vehicleID,
  fenceID
);

-- ---------------------------------------------------------------------
-- Gatilho para processar atualizações na tabela de veículos por cerca
-- ---------------------------------------------------------------------
-- Cria um gatilho que lida com as atualizações de registros na tabela
-- de veículos habilitados por cerca, atualizando a informação de que a
-- cerca geográfica é válida para todos os veículos do cliente.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION processVehiclesEnabledByFences()
RETURNS TRIGGER AS
$BODY$
  BEGIN
    IF TG_OP = 'INSERT' THEN
      -- Indicamos que a cerca geográfica não é válida para todos os
      -- veículos, já que um veículo foi habilitado para ela
      UPDATE public.geofences
        SET validForEveryone = FALSE
      WHERE geoObjectID = NEW.fenceID;

      -- Retornamos a nova entidade
      RETURN NEW;
    ELSIF TG_OP = 'DELETE' THEN
      IF NOT EXISTS (SELECT 1 FROM public.vehiclesEnabledByFences WHERE fenceID = OLD.fenceID) THEN
        -- Indicamos que a cerca geográfica é válida para todos os
        -- veículos, já que nenhum veículo está habilitado para ela
        UPDATE public.geofences
          SET validForEveryone = TRUE
        WHERE geoObjectID = OLD.fenceID;
      END IF;
    END IF;

    RETURN OLD;
  END;
$BODY$ LANGUAGE plpgsql;

-- DROP TRIGGER IF EXISTS vehicleEnabledByFenceTransactionTrigger ON vehiclesEnabledByFences;
CREATE TRIGGER vehicleEnabledByFenceTransactionTrigger
  AFTER INSERT OR DELETE ON vehiclesEnabledByFences
  FOR EACH ROW EXECUTE PROCEDURE processVehiclesEnabledByFences();

-- ---------------------------------------------------------------------
-- Função para obter o nome da cerca
-- ---------------------------------------------------------------------
-- Cria uma função que obtém qual o nome da cerca geográfica.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION formatFenceName(temporary boolean, name text)
RETURNS text AS $$
BEGIN
  IF temporary THEN
    RETURN 'temporária';
  ELSE
    RETURN '‛' || name || '’';
  END IF;
END;
$$ LANGUAGE plpgsql;

-- ---------------------------------------------------------------------
-- Função para obter as cercas geográficas onde o veículo encontra-se
-- ---------------------------------------------------------------------
-- Cria uma função que obtém qual(is) cerca(s) geográfica(s) contém a
-- coordenada geográfica de um veículo.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION getFencesWhereItIsInserted(FcustomerID integer,
  Flatitude float, Flongitude float, FvehicleID integer)
RETURNS text AS $$
DECLARE
  areas text;
BEGIN
  IF FvehicleID IS NULL THEN
    RETURN NULL;
  END IF;

  SELECT STRING_AGG(public.formatFenceName(fence.temporary, fence.name), ', ') INTO areas
    FROM public.geofences AS fence
    LEFT JOIN public.vehiclesEnabledByFences AS vehicle ON fence.geoObjectID = vehicle.fenceID AND vehicle.vehicleID = FvehicleID
   WHERE fence.customerID = FcustomerID
     AND public.ST_Contains(
           area,
           public.ST_SetSRID(
            public.ST_MakePoint(Flongitude, Flatitude),
            4326
           )
         )
     AND (fence.validForEveryone = TRUE OR vehicle.vehicleID IS NOT NULL)
     AND fence.disabled = FALSE;

  IF areas IS NULL THEN
    RETURN '';
  ELSIF strpos(areas, ', ') > 0 THEN
    RETURN 'dentro das cercas ' || regexp_replace(areas, ', (?=[^,]*$)', ' e ');
  ELSE
    RETURN 'dentro da cerca ' || areas;
  END IF;
END;
$$ LANGUAGE plpgsql;

