-- ---------------------------------------------------------------------
-- Função para obter se o veículo possui uma cerca temporária definida
-- ---------------------------------------------------------------------
-- Cria uma função que obtém se o veículo possui uma cerca temporária
-- definida.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION getHasTemporaryFence(FvehicleID integer)
RETURNS boolean AS $$
DECLARE
  result boolean;
BEGIN
  SELECT INTO result
         CASE
          WHEN EXISTS (
            SELECT 1
              FROM public.vehiclesEnabledByFences AS vehicle
              INNER JOIN public.geofences AS fence ON vehicle.fenceID = fence.geoobjectid
              WHERE vehicle.vehicleID = FvehicleID
                AND fence.temporary = true
          ) THEN TRUE
          ELSE FALSE
        END AS VehicleInTemporaryFence;
  
  RETURN result;
END;
$$ LANGUAGE plpgsql;

