-- ---------------------------------------------------------------------
-- Função para obter eventos de cerca
-- ---------------------------------------------------------------------
-- Cria uma função que obtém se um veículo entrou ou saíu de uma cerca
-- atrabés das coordenadas geográficas do local, do ID do veículo e do
-- ID do cliente. Retorna os eventos de cerca ocorridos.
-- ---------------------------------------------------------------------
CREATE OR REPLACE FUNCTION getFencesAction(FcustomerID integer,
  Flatitude float, Flongitude float, FvehicleID integer)
RETURNS int[] AS $$
DECLARE
  fenceRow record;
  hasFenceIn boolean;
  hasFenceOut boolean;
  fenceEventOccurred int[];
BEGIN
  -- RAISE NOTICE 'Check if point (%, %) is inside of a fence',
  --   Flatitude, Flongitude;

  -- Inicializa as variáveis
  hasFenceIn := FALSE;
  hasFenceOut := FALSE;

  FOR fenceRow IN 
    WITH point_of_interest AS (
      SELECT public.ST_SetSRID(public.ST_MakePoint(Flongitude, Flatitude), 4326) as geom
    )
    SELECT fence.geoObjectID AS id,
           fence.name,
           fence.temporary,
           CASE
             WHEN fence.validForEveryone = TRUE THEN fence.notification
             WHEN fence.validForEveryone = FALSE
              AND vehicle.notification = 'DEFAULT' THEN fence.notification
             ELSE vehicle.notification
           END AS notification,
           EXISTS (SELECT 1 FROM public.vehiclesInFences WHERE vehicleID = FvehicleID AND fenceID = fence.geoObjectID)  AS inside
      FROM point_of_interest,
           public.geofences AS fence
      LEFT JOIN public.vehiclesEnabledByFences AS vehicle ON fence.geoObjectID = vehicle.fenceID AND vehicle.vehicleID = FvehicleID
     WHERE fence.customerID = FcustomerID
       AND public.ST_Contains(
             area,
             point_of_interest.geom
           )
       AND (fence.validForEveryone = TRUE OR vehicle.vehicleID IS NOT NULL)
       AND fence.disabled = FALSE
  LOOP
    IF fenceRow.inside = FALSE THEN
      -- RAISE NOTICE 'Inside of a fence %', fenceRow.name;

      IF fenceRow.notification = 'BOTH' OR fenceRow.notification = 'ENTER' THEN
        IF fenceRow.temporary = TRUE THEN
          -- RAISE NOTICE 'Ignore enter notification in temporary fence %', fenceRow.name;
        ELSE
          -- RAISE NOTICE 'Add enter notification in fence %', fenceRow.name;
          hasFenceIn := TRUE;
        END IF;
      END IF;

      -- Sempre insere o veículo na cerca, se ainda não estiver
      INSERT INTO public.vehiclesInFences (vehicleID, fenceID)
        VALUES (FvehicleID, fenceRow.id);
    END IF;
  END LOOP;

  IF hasFenceIn THEN
    -- Geramos um evento de cerca
    fenceEventOccurred := array_append(fenceEventOccurred, 44);
  END IF;

  -- Agora verifica se o veículo saiu de alguma cerca que ele estava.
  -- Como armazenamos os ID's das cercas que o veículo está inserido,
  -- podemos verificar se ele saiu de alguma cerca usando estes ID's.
  FOR fenceRow IN 
    SELECT fence.geoObjectID AS id,
           fence.name,
           fence.temporary,
           CASE
             WHEN fence.validForEveryone = TRUE THEN fence.notification
             WHEN fence.validForEveryone = FALSE
              AND vehicle.notification = 'DEFAULT' THEN fence.notification
             ELSE vehicle.notification
           END AS notification,
           public.ST_Contains(
              area,
              public.ST_SetSRID(
                public.ST_MakePoint(Flongitude, Flatitude),
                4326
              )
           ) AS inside
      FROM public.vehiclesInFences AS history
     INNER JOIN public.geofences AS fence ON (history.fenceID = fence.geoObjectID)
      LEFT JOIN public.vehiclesEnabledByFences AS vehicle ON fence.geoObjectID = vehicle.fenceID AND vehicle.vehicleID = FvehicleID
    WHERE history.vehicleID = FvehicleID
      AND fence.customerID = FcustomerID
      AND (fence.validForEveryone = TRUE OR vehicle.vehicleID IS NOT NULL)
      AND fence.disabled = FALSE
  LOOP
    IF fenceRow.inside = FALSE THEN
      -- RAISE NOTICE 'Outside of a fence %', fenceRow.name;

      IF fenceRow.notification = 'BOTH' OR fenceRow.notification = 'EXIT' THEN
        -- RAISE NOTICE 'Add exit notification in fence %', fenceRow.name;
        hasFenceOut := TRUE;
      END IF;

      -- Remove o veículo da cerca, se estiver
      DELETE FROM public.vehiclesInFences
       WHERE vehicleID = FvehicleID
         AND fenceID = fenceRow.id;
      
      -- Remove a cerca se a mesma for temporária
      IF fenceRow.temporary = TRUE THEN
        -- RAISE NOTICE 'Remove temporary fence %', fenceRow.name;
        DELETE FROM public.vehiclesEnabledByFences
          WHERE fenceID = fenceRow.id;
        DELETE FROM public.geofences
          WHERE geoObjectID = fenceRow.id;
      END IF;
    END IF;
  END LOOP;

  IF hasFenceOut THEN
    -- Geramos um evento de cerca
    fenceEventOccurred := array_append(fenceEventOccurred, 45);
  END IF;

  RETURN fenceEventOccurred;
END;
$$ LANGUAGE plpgsql;

